(116, 'Dr. Claudio Leonardo Andrade', 'Mestre e Especialista em Prótese Dental <br>CRO-DF 3238', '<p dir=\"ltr\">Dr. Claudio Leonardo Andrade (CRO-DF 3238) é Mestre e Especialista em Prótese Dental, com especialização em Implantodontia e Periodontia. Também atua como Coordenador do Curso de Especialização em Prótese e Implante no IOA em Brasília, conhecido pelo tratamento odontológico de excelência.</p>\r\n', '1688055944.8475-foto-N.webp', NULL, 'Dr. Claudio| Esthetical Odontologia | (61) 99129-5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Mestre e Especialista em Prótese Dental que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dr-claudio-leonardo-andrade', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, '1688055786.2484-foto2-N.webp'),
(117, 'Dr. Érico Vinícius Andrade', 'Especialista em Ortodontia e Ortopedia Facial <br> CRO-DF 8883', '<p dir=\"ltr\">Dr. Érico Vinícius Andrade (CRO-DF 8883) é Especialista em Ortodontia e Ortopedia Facial, com especialização em Disfunção Temporomandibular e Dor Orofacial. Além disso, é professor do Curso de Especialização em Ortodontia no IOA em Brasília, trazendo sua expertise para o ensino e prática dessa área.</p>\r\n', NULL, NULL, 'Dr. Érico| Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Especialista em Ortodontia e Ortopedia Facial que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dr-Erico-vinicius-andrade', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(118, 'Dra. Amanda Oliveira', 'Especialista em Prótese Dentária <br> CRO-DF 9136', '<p dir=\"ltr\"><strong>Dra. Amanda Oliveira (CRO-DF 9136) é Especialista em Prótese Dentária e Cirurgiã Dentista na Força Aérea Brasileira - FAB. Ela também é professora do curso de especialização em Prótese Dentária no IOA em Brasília, combinando sua experiência clínica com o desejo de compartilhar o melhor de seu conhecimento.</strong></p>\r\n', '', NULL, 'Dra. Amanda| Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Especialista em Prótese Dentária que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dra-amanda-oliveira', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, ''),
(119, 'Dr. Marcelo Guedes', 'Especialista em Periodontia e Prótese Dental <br> CRO-DF 6968', '<p dir=\"ltr\">Dr. Marcelo Guedes (CRO-DF 6968) é um especialista em Periodontia e Prótese Dental altamente capacitado para proporcionar cuidados especializados. Sua experiência e conhecimento garantem tratamentos eficazes e resultados de alta qualidade, visando a saúde bucal e o bem-estar de cada paciente.</p>\r\n', NULL, NULL, 'Dr. Marcelo| Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Especialista em Periodontia e Prótese Dental que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dr-marcelo-guedes', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(120, 'Dra. Adriana Abdalla', 'Especialista em Endodontia <br> CRO-DF 3700', '<p dir=\"ltr\">Dra. Adriana Abdalla (CRO-DF 3700) é uma especialista em Endodontia altamente qualificada, dedicada ao tratamento de canal e procedimentos relacionados. Com sua expertise e cuidado, ela proporciona soluções precisas visando a preservação da saúde dos dentes e garantir o conforto dos pacientes.</p>\r\n', NULL, NULL, 'Dra. Adriana | Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Especialista em Endodontia que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dra-adriana-abdalla', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(121, 'Dra. Ludmila Madeira Pavan', 'Especialista em Periodontia e Implantodontia <br> CRO-DF 7039', '<p dir=\"ltr\">Dra. Ludmila Madeira Pavan (CRO-DF 7039) é Especialista em Periodontia e Implantodontia, com mestrado em Ciências da Saúde. Ela possui habilitação em Odontologia Hospitalar, Laserterapia e Ozonioterapia. Além disso, é professora de periodontia e implantodontia.</p>\r\n', NULL, NULL, 'Dra. Ludmila | Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'specialista em Periodontia e Implantodontia que trabalha de forma integrada para promover os melhores tratamentos para você. ', '', NULL, NULL, NULL, NULL, 'dra-ludmila-madeira-pavan', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(122, 'Dra. Gabriela de Oliveira Sousa', 'Clínica Geral <br> CRO-DF 13.399', '<p dir=\"ltr\">Dra. Gabriela de Oliveira Sousa (CRO-DF 13.399) é uma dentista clínica geral com ampla experiência em reabilitação, dentística restauradora e periodontia. Atualmente, está em processo de especialização em prótese dentária e implantodontia, enfatizando sua busca contínua por aprimoramento profissional.</p>\r\n', '', NULL, 'Dra. Gabriela| Esthetical Odontologia | (61) 99129 - 5018', 'odontologia estética, odontologia estética em brasília, clínica odontológica em brasília, dentista asa norte, clínica odontológica na asa norte, dentista em brasília, dentística, dentística restauradora, reabilitação oral, harmonização orofacial, dentista brasília, reabilitação dentária, dentista harmonização, dentista orofacial, clínica odontológica asa norte.', 'Dentista clínica geral que trabalha de forma integrada para promover os melhores tratamentos para você. \r\n', '', NULL, NULL, NULL, NULL, 'dra-gabriela-oliveira-sousa', NULL, '', '', '', '', NULL, NULL, NULL, NULL, NULL, '');
